import { useState, useEffect, useMemo } from "react"
import './styles.css';
import Box from "./Box";

function App() {



    let renderedBoxes;
    const [turn, setTurn] = useState("X")
    const [msg, setMsg] = useState("")
    const [turnArr, setTurnArr] = useState(["", "", "", "", "", "", "", "", ""])
    const [counter, setCounter] = useState(0)


    const handleClick = (id) => {

        if (counter <= 9) {
            if (turnArr[id] !== "" && counter > 0) {
                return
            }
            setCounter(counter + 1)
            let newTurnarr = turnArr.map((item, index) => {
                if (index === id) {
                    return turn
                }
                return item
            })
            setTurnArr(newTurnarr)
            if (turn === "X") {
                setTurn("O")
            }
            else {
                setTurn("X")
            }

        }
    }

    // eslint-disable-next-line react-hooks/exhaustive-deps
    const renderBoxFunc = () => {
        console.log(2)
        renderedBoxes = turnArr.map((item, index) => {
            return <Box id={index} key={index} onChange={handleClick}>{item}</Box>
        })
    }

    useMemo(() => {
        renderBoxFunc();
        console.log(1)
    }, [turnArr])

    const winArray = [
        [0, 1, 2],
        [3, 4, 5],
        [6, 7, 8],
        [0, 3, 6],
        [1, 4, 7],
        [2, 5, 8],
        [0, 4, 8],
        [2, 4, 6]]

    let content = msg === "" ? `${turn}'s turn` : msg

    return (
        <div className="wrapper">
            <h1>Tic Tac Toe</h1>
            <div className="boxes">
                {renderedBoxes}
            </div>
            <div className="message-area">{content}</div>
        </div>
    )
}


export default App
